import ExpenseItem from "./ExpenseItem";

import './ExpensesList.css';

const ExpensesList = props => {

    // if the array is empty, show a message by default screen
    let expensesContent= <p>No expenses found.</p>


    // OTHERWISE, check if the array has items and update 'expensesContent' to show on screen only the filtered expenses by year
    if (props.items.length > 0) {
    expensesContent = props.items.map((expense) => (
        <ExpenseItem 
        key={expense.id}
        title={expense.title} 
        amount={expense.amount} 
        date={expense.date}
        />
    ));
    } 

    return <ul></ul>
};

export default ExpensesList;
