import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

function Expenses(props) {

  const [year, setYear] = useState('2023');

  function filterChangeHandler (selectedYear) {
    // update the year that user selected
    setYear(selectedYear);
  };


  // filter() -> built in function that receives another function and returns a new array if it's true
  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === year;
  });


  return (
    <div>
      <Card className="expenses">

        <ExpensesFilter onChangeFilter={filterChangeHandler} defaultYear={year} />

        {/* check if the array is empty and show a message on screen, OTHERWISE, filter and show on screen only the filtered expenses by year */}
        {filteredExpenses.length === 0 ? (
          <p>No expenses found.</p>
        ) : (
          filteredExpenses.map((expense) => (
          <ExpenseItem 
            key={expense.id}
            title={expense.title} 
            amount={expense.amount} 
            date={expense.date}
          />
          ))
        )}

      </Card>
    </div>

  );
}

export default Expenses;
