import { useState } from 'react';
import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {

    const [showExpenseForm, setShowExpenseForm] = useState(false);
    let [activeButton, setActiveButton] = useState(null);

    const addExpenseFormHandler = () => {
        setShowExpenseForm(true);
        setActiveButton(1)
    }

    let buttonToAdd = <button onClick={addExpenseFormHandler} >Add New Expense</button>


    const saveExpenseDataHandler = (newExpenseData) => {
        const expenseData = {
            ...newExpenseData,
            id: Math.random().toString()
        };

        // data from child to parent: NewExpense -> App
        props.onAddExpense(expenseData);
    }; 

    return (
        <div className='new-expense'>
            <button></button>

            <ExpenseForm onSaveNewExpenseData={saveExpenseDataHandler}/>
        </div>
    )
};

export default NewExpense;

