import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

function Expenses(props) {

  const [year, setYear] = useState('2023');

  function filterChangeHandler (selectedYear) {
    // get the year that user selected
    setYear(selectedYear);


    // filter the year and show on screen just expenses of that year

    // get the year of each product of the expenses list array
    const array = [2022,2023,2021].filter(checkYear);

    function checkYear(x) {

      if (x === selectedYear) {
        return x;
      }
    }

    return console.log(array)
  };

  return (
    <div>
      <Card className="expenses">

        <ExpensesFilter onChangeFilter={filterChangeHandler} defaultYear={year} />

        {props.items.map(expense => (
          <ExpenseItem 
            key={expense.id}
            title={expense.title} 
            amount={expense.amount} 
            date={expense.date}/>
          ))
        }

      </Card>
    </div>

  );
}

export default Expenses;
